import React, { useEffect } from 'react';
import { Paper, Typography, CircularProgress, Divider, CardActions } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { useParams, useHistory } from 'react-router-dom';

import Comment from '../Posts/Post/Comment';
import { getPost } from '../../actions/posts';
import useStyles from './styles';

const PostUnique = () => {
    const { post, posts, isLoading } = useSelector((state) => state.posts);
    const dispatch = useDispatch();
    const history = useHistory();
    const classes = useStyles();
    const { id } = useParams();
    const user = JSON.parse(localStorage.getItem('profile'));

    useEffect(() => {
        dispatch(getPost(id));
    }, [id]);


    if (!post) return null;



    if (isLoading) {
        return (
            <Paper elevation={6} className={classes.loadingPaper}>
                <CircularProgress size="7em" />
            </Paper>
        );
    }

    return (
        <Paper style={{ padding: '20px', borderRadius: '15px' }} elevation={6}>
            <div className={classes.card}>
                <div className={classes.section}>
                    <Typography variant="h3" component="h2">{post.title}</Typography>
                    <Typography gutterBottom variant="h6" color="textSecondary" component="h2">{post.tags.map((tag) => `#${tag} `)}</Typography>
                    <Typography gutterBottom variant="body1" component="p">{post.message}</Typography>
                    <Typography variant="h6">Created by: {post.name}</Typography>
                    <Typography variant="body1">{moment(post.createdAt).fromNow()}</Typography>
                    <Divider style={{ margin: '20px 0' }} />
                    <Comment post={post} />
                    <Divider style={{ margin: '20px 0' }} />
                    <CardActions className={classes.cardActions}>
                        <Button size="small" color="primary" disabled={!user?.result} onClick={handleLike}>
                            <Likes />
                        </Button>
                        {(user?.result?.googleId === post?.creator || user?.result?._id === post?.creator || user?.result?.role === 'admin') && (
                            <Button size="small" color="secondary" onClick={() => dispatch(deletePost(post._id))}>
                                <DeleteIcon fontSize="small" /> Supprimer
                            </Button>
                        )}
                        {(user?.result?.googleId === post?.creator || user?.result?._id === post?.creator || user?.result?.role === 'admin') && (

                            <Button onClick={() => setCurrentId(post._id)} style={{ color: '#4e5166' }} size="small">
                                <MoreHorizIcon fontSize="default" /> Modifier
                            </Button>
                        )}
                    </CardActions>
                </div>
                <div className={classes.imageSection}>
                    <img className={classes.media} src={post.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} alt={post.title} />
                </div>
            </div>
        </Paper>
    );
};

export default PostUnique;
