import { createStyles, makeStyles } from "@material-ui/core";

export default makeStyles((theme) => ({
    container: {
        display: 'flex',
    },
    formContainer: {
        with: '20%',
        height: 'fit-content',
    },
    [theme.breakpoints.down('sm')]: {

        mainContainer: {
            flexDirection: "column-reverse",
        }
    }

}))
