import React, { useState } from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography, Box, Divider, Paper } from '@material-ui/core/';
import Modal from '@material-ui/core/Modal'
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import ThumbUpAltOutlined from '@material-ui/icons/ThumbUpAltOutlined';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom'
import Comment from './Comment';
import moment from 'moment';
// Tooltip
import Tooltip from '@material-ui/core/Tooltip';
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';

import { likePost, deletePost } from '../../../actions/posts';
import useStyles from './styles';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

const Post = ({ post, setCurrentId }) => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const user = JSON.parse(localStorage.getItem('profile'));
  // Moddal
  const [openPostModal, setOpenPostModal] = useState(false);

  const Likes = () => {
    if (post.likes.length > 0) {
      return post.likes.find((like) => like === user?.result?._id)
        ? (
          <><ThumbUpAltIcon fontSize="small" />&nbsp;{post.likes.length > 2 ? `Vous et ${post.likes.length - 1} autres` : `${post.likes.length} like${post.likes.length > 1 ? 's' : ''}`}</>
        ) : (
          <><ThumbUpAltOutlined fontSize="small" />&nbsp;{post.likes.length} {post.likes.length === 1 ? 'Like' : 'Likes'}</>
        );
    }

    return <><ThumbUpAltOutlined fontSize="small" />&nbsp;Like</>;
  };

  return (
    <>
      <Tooltip onClick={(e) => setOpenPostModal(true)} title="Ajouter un post" aria-label="add">
        <Fab className={classes.absolute}>
          <AddIcon />
        </Fab>
      </Tooltip>
      <Modal
        open={openPostModal}
        onClose={(e) => setOpenPostModal(false)}
        aria-labelledby="fenêtre-du-post"
        aria-describedby="fenêtre-ajout-post">
        <div className={classes.paperModal}>
          <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
            <Typography variant="h6">{currentId ? `Modifier "${post.title}"` : 'Creer'}</Typography>
            <TextField name="title" variant="outlined" label="Titre" fullWidth value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value })} />
            <TextField ref={textRef} name="message" variant="outlined" label="Message" fullWidth multiline rows={4} value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value })} />
            <div className={classes.emojiWrapper}>
              {picker && (
                <div className={classes.emoji}><Picker onEmojiClick={handleEmoji} /></div>
              )}
              <img className={classes.emojiImage} src={colorFull} alt="" />
              <EmojiEmotionsOutlined onClick={() => { setPicker((prev) => !prev) }} className={classes.emojiIcon} />
            </div>
            <TextField name="tags" variant="outlined" label="Tags (séparés par des virgules)" fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(',') })} />
            <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
            <Button className={classes.buttonSubmit} variant="contained" size="large" type="submit" fullWidth>Poster</Button>
            <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Effacer</Button>
          </form>
        </div>
      </Modal>
      <Paper style={{ margin: '15px auto', padding: '20px', borderRadius: '15px' }} elevation={6}>
        <div className={classes.card}>
          <div className={classes.section}>
            <Typography variant="h3" component="h2">{post.title}</Typography>
            <Typography gutterBottom variant="h6" color="textSecondary" component="h2">{post.tags.map((tag) => (
              <Link to={`/tags/${tag}`} style={{ textDecoration: 'none', color: '##fd2d01' }}>
                {` #${tag} `}
              </Link>
            ))}
            </Typography>
            <Typography gutterBottom variant="body1" component="p">{post.message}</Typography>
            <Typography variant="h6">
              Created by:
              {` ${post.name}`}
            </Typography>
            <Typography variant="body1">{moment(post.createdAt).fromNow()}</Typography>
            <Divider style={{ margin: '20px 0' }} />
            <Typography variant="body1"><strong>Realtime Chat - coming soon!</strong></Typography>
            <Divider style={{ margin: '20px 0' }} />
            <Comment post={post} />
            <Divider style={{ margin: '20px 0' }} />
            <CardActions className={classes.cardActions}>
              <Button size="small" color="primary" disabled={!user?.result} onClick={() => dispatch(likePost(post._id))}>
                <Likes />
              </Button>
              {(user?.result?.googleId === post?.creator || user?.result?._id === post?.creator || user?.result?.role === 'admin') && (
                <Button size="small" color="secondary" onClick={() => dispatch(deletePost(post._id))}>
                  <DeleteIcon fontSize="small" /> Supprimer
                </Button>
              )}
              {(user?.result?.googleId === post?.creator || user?.result?._id === post?.creator || user?.result?.role === 'admin') && (

                <Button onClick={() => setCurrentId(post._id)} style={{ color: '#4e5166' }} size="small">
                  console.log(post._id)
                  <MoreHorizIcon fontSize="default" />
                </Button>
              )}
            </CardActions>
          </div>
          <div className={classes.imageSection}>
            <img className={classes.media} src={post.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} alt={post.title} />

          </div>
        </div>
      </Paper>
    </>
  );
};

export default Post;
