import React, { useState } from 'react'
import { Typography, TextField, Button } from '@material-ui/core'
import { useDispatch } from 'react-redux'

import useStyles from './styles'

const Comment = ({ post }) => {
    console.log(post)
    const classes = useStyles()
    const [comments, setComments] = useState([1, 2, 3, 4])
    const [comment, setComment] = useState('')
    const user = JSON.parse(localStorage.getItem('user'))

    const handleClick = () => {
        dispatch(commentPost())
    }
    return (
        <div>
            <div className={classes.commentsWrapper}>
                <div className={classes.commentsContainer}>
                    <Typography gutterBottom variant='h6'>Comments</Typography>
                    {comments.map((c, i) => (
                        <Typography key={i} gutterBottom variant="subtitle1">
                            Comment: {i}
                        </Typography>
                    ))}
                </div>
                <div style={{ width: '70%' }}>
                    <Typography gutterBottom variant='h6'>Votre commentaire</Typography>
                    <TextField
                        fullWidth
                        rows={3}
                        variant='outlined'
                        label='Commentaire'
                        multiline
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                    />
                    <Button className={classes.btnComment} style={{ marginTop: '10px' }} fullWidth disabled={!comment} variant='contained' onClick={handleClick}>
                        Commenter
                    </Button>
                </div>
            </div>
        </div>
    )
}

export default Comment
